mapper.select:
SELECT ID, CONTRACT_ID, RFID_NO, AMOUNT, BATCH_ITEMS,BATCH_NO,UPDATE_STATE FROM M_CONTRACT_BATCH
	WHERE 1=1 
	

	AND CONTRACT_ID = #{condition.contractId}


	AND RFID_NO = #{condition.rfidNo}


	AND UPDATE_STATE != #{condition.state}

		
			SELECT C.BATCH_NO,C.AMOUNT,C.BATCH_ITEMS,C.RFID_NO FROM M_CONTRACT_BATCH C WHERE C.UPDATE_STATE !=1 AND C.RFID_NO=#{condition.rfidNo} UNION ALL 
 			SELECT M.BATCH_NO,M.AMOUNT,M.BATCH_ITEMS,M.RFID_NO FROM M_CONTRACT_MODIFY_BATCH M   WHERE M.UPDATE_STATE !=1 AND M.RFID_NO=#{condition.rfidNo} UNION ALL 
			SELECT R.BATCH_NO,R.AMOUNT,R.BATCH_ITEMS,R.NEW_RFID_NO AS RFID_NO FROM M_CONTRACT_REPLENISH_BATCH R WHERE R.NEW_RFID_NO=#{condition.rfidNo}
